import React, { useState } from "react";
import "../input-fields/input-fields.css";

function Input() {
  const [placeholder, setPlaceholder] = useState("Tekstą įkelkite čia");

  const handleInputButtonClick = (event) => {
    setPlaceholder(event.currentTarget.getAttribute("data-placeholder"));
  };

  return (
    <div className="input-fields">
      <div className="buttons">
        <button
          type="button"
          data-input-type="text"
          data-placeholder="Tekstą įkelkite čia"
          onClick={handleInputButtonClick}
        >
          Iš teksto<span>Struktūrizuoti iš teksto</span>
        </button>
        <button
          type="button"
          data-input-type="link"
          data-placeholder="Įkelkite nuorodą į privatumo politiką"
          onClick={handleInputButtonClick}
        >
          Iš nuorodos<span>Struktūrizuoti iš nuorodos</span>
        </button>
        <button
          type="button"
          data-input-type="file"
          data-placeholder="Dokumentą nuvilkite čia arba"
          onClick={handleInputButtonClick}
        >
          Iš failo<span>Struktūrizuoti iš .pdf</span>
        </button>
      </div>
      <div className="input-field">
        <input placeholder={placeholder}></input>
      </div>
    </div>
  );
}

export default Input;
