import React, { useState } from "react";
import axios from "axios";

const StudentForm = ({ classId, className, setRefresh }) => {
  const [studentFullName, setStudentFullName] = useState("");
  const [grades, setGrades] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!studentFullName || !grades) {
      setError("Both Name and Grades are required.");
      return;
    }

    const gradesArray = grades
      .split(",")
      .map((grade) => parseInt(grade.trim(), 10))
      .filter((grade) => !isNaN(grade));

    if (gradesArray.length === 0) {
      setError("Please provide valid grades.");
      return;
    }

    try {
      await axios.post("http://localhost:5000/students", {
        studentFullName,
        grades: gradesArray,
        classId,
        className: className,
        studentId: Math.random().toString(36).substring(2, 9), //example studentId
      });

      setRefresh((prev) => !prev);
      setStudentFullName("");
      setGrades("");
      setError("");
    } catch (err) {
      setError("Error adding student.");
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Add Student</h3>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <input
        placeholder="Name and surname"
        value={studentFullName}
        onChange={(e) => setStudentFullName(e.target.value)}
      />
      <input
        placeholder="Grades (comma-separated)"
        value={grades}
        onChange={(e) => setGrades(e.target.value)}
      />
      <button type="submit">Add</button>
    </form>
  );
};

export default StudentForm;
