import React, { useState } from "react";

const PrivacyForm = () => {
  const [error, setError] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [fileName, setFileName] = useState("");
  const [inputType, setInputType] = useState("text");

  const handleSubmit = () => {
    console.log("Form submitted with input:", inputValue, "file:", fileName);
    console.log(inputType);

    if (inputType === "text") {
      handleTextInput(inputValue);
    } else if (inputType === "link") {
      // Handle link input type
    } else if (inputType === "file") {
      // Handle file input type
    }
  };

  const handleTextInput = (inputValue) => {
    const MIN_INPUT_LENGTH = 1000;

    if (inputValue.length < MIN_INPUT_LENGTH) {
      setError("Patikrinkite ar įklijavote pilną privatumo politikos tekstą");
    } else {
      setError(""); // Clear error if input is valid
    }
  };

  return (
    <div>
      <textarea
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Įklijuokite privatumo politiką"
      />
      <button onClick={handleSubmit}>Pateikti</button>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default PrivacyForm;
