import React, { useEffect, useState } from "react";
import axios from "axios";

const StudentList = ({ classId }) => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    if (classId) {
      axios
        .get(`http://localhost:5000/classes/${classId}/students`)
        .then((response) => {
          setStudents(response.data);
        });
    }
  }, [classId]);

  return (
    <div>
      <h2>Students</h2>
      <ul>
        {students.map((student) => (
          <li key={student._id}>
            {student.className} {student.studentFullName} ({student.studentId})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentList;
